package dk.dmi.lib.db;import dk.dmi.lib.location.GridPoint;import java.sql.PreparedStatement;import java.sql.ResultSet;import java.sql.SQLException;import java.util.ArrayList;/** * * @author Kenan Vilic, kev@dmi.dk */public class NovanaDB extends Database {    public NovanaDB(String url, String user, String password, String catalog) {        super(url, user, password, Database.MYSQL);        this.catalog = catalog;    }    public ArrayList<GridPoint> ReadGridpoints() {        ArrayList<GridPoint> gps = new ArrayList<>(200);        try {            String statement = "SELECT field_id, eastings_z32, northings_z32 FROM novana2DB.grid_field_id_pos WHERE grid_size = '20km'";            PreparedStatement ps = getConnection().prepareStatement(statement);            ResultSet r = ps.executeQuery();            while (r.next()) {                int fieldid = r.getInt("field_id");                int eastings = r.getInt("eastings_z32");                int northings = r.getInt("northings_z32");                GridPoint gp = new GridPoint(fieldid, eastings, northings, 20000);                gps.add(gp);            }        } catch (SQLException sqlex) {            sqlex.printStackTrace();        } catch (Exception ex) {            ex.printStackTrace();        }        return gps;    }}